def isPrime(num):
    # 首先判断如果输入的数小于等于1的话，一定不是质数
    if num <= 1:
        return False
    # 然后判断从2开始到num-1的所有数是否能整除num
    for i in range(2, num):
        if num % i == 0:
            return False
    # 如果没有找到能整除的数，那就说明num是质数
    return True

